        qDebug() << "DEBUG:" << QDateTime::currentDateTime().toString() << " [" << context.file << ":" << context.line << "][" << context.function << "] " << localMsg.constData();
        break;
    case QtInfoMsg:
        qDebug() << "INFO:" << QDateTime::currentDateTime().toString() << " [" << context.file << ":" << context.line << "][" << context.function << "] " << localMsg.constData();
        break;
    case QtWarningMsg:
        qDebug() << "WARNING:" << QDateTime::currentDateTime().toString() << " [" << context.file << ":" << context.line << "][" << context.function << "] " << localMsg.constData();
        break;
    case QtCriticalMsg:
        qDebug() << "CRITICAL:" << QDateTime::currentDateTime().toString() << " [" << context.file << ":" << context.line << "][" << context.function << "] " << localMsg.constData();
        break;
    case QtFatalMsg:
        qDebug() << "FATAL:" << QDateTime::currentDateTime().toString() << " [" << context.file << ":" << context.line << "][" << context.function << "] " << localMsg.constData();
        break;
    }

    if (type == QtFatalMsg)
    {
        Q_ASSERT(false);
    }
}

int main(int argc, char *argv[])
{
    qInstallMessageHandler(myMessageHandler);

    QDir logDir("/path/to/logs");
    if (!logDir.exists())
